import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet'
import 'leaflet/dist/leaflet.css';
import "./map.css";
import {Icon} from 'leaflet';
import { LeafletContext } from "@react-leaflet/core";
import foto_icono from "./punto.png";

const Map = ({socket})=> {
    const [mapa, setMapa] = useState([]);
    const [flights, setFlights] = useState([]);
    const foto = new Icon({
        iconUrl: foto_icono,
        iconSize: [5, 5],
    })

    useEffect(() => {

      socket.on("POSITION", data => {
        setMapa((prev)=> [...prev, data]);
      });
      socket.emit("FLIGHTS")
      socket.on("FLIGHTS", (data) => {
        setFlights(data)
      });

    }, []);
    const inicio = []
    flights.map(vuelo => {inicio.push([vuelo.origin, vuelo.destination])

    })
  inicio.map(coordenada =>{
      console.log(coordenada)
  })
    return (
      <div>

            <MapContainer center={[-35.675147, -71.542969]} zoom={5} scrollWheelZoom={false}>
            <TileLayer
            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />
            {mapa.map(posicion => {
            if (posicion){ return (
                <Marker position={[posicion.position[0], posicion.position[1]]} icon={foto}>
                <Popup>
                {posicion.code}
                </Popup>
            </Marker>)}})}

            {inicio.map(coordenadas =>{
            <Polyline

            latlng={coordenadas}>

            </Polyline>})}

            </MapContainer>


       <h4 id="info-vuelos">Información Vuelos </h4>

        <div className="container_informacion"> {flights.map(vuelos => {
                  if (vuelos){return(
                      <div className="informacion">
                          {vuelos.code}
                          <ul>
                          <li>Aerolínea: {vuelos.airline}</li>
                          <li>Origen: {vuelos.origin}</li>
                          <li>Destino: {vuelos.destination}</li>
                          <li>Avión: {vuelos.plane}</li>
                          <li>Asientos: {vuelos.seats}</li>
                          <li>Pasajeros:{vuelos.passengers.map(pasajero => {
                              if (pasajero){return(
                                  <p>{pasajero.name}, {pasajero.age}</p>
                              )}
                          })}</li>
                      </ul>
                      </div>


                  )}
              })}</div>
       </div>



    );
}

export default Map;
