import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'
import 'leaflet/dist/leaflet.css';
import "./map.css";
import {Icon} from 'leaflet';
import { LeafletContext } from "@react-leaflet/core";
import foto_icono from "./punto.png";

const Map = ({socket})=> {
    const [mapa, setMapa] = useState([]);
    const foto = new Icon({
        iconUrl: foto_icono,
        iconSize: [10, 10],
    })

    useEffect(() => {

      socket.on("POSITION", data => {
        setMapa((prev)=> [...prev, data]);
      });
    }, []);

    return (
      <div>
        <MapContainer center={[-35.675147, -71.542969]} zoom={3} scrollWheelZoom={false}>
        <TileLayer
        attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {mapa.map(posicion => {
        if (posicion){ return (
            <Marker position={[posicion.position[0], posicion.position[1]]} icon={foto}>
            <Popup>
            {posicion.code}
            </Popup>
        </Marker>)}})}

        </MapContainer>

       <p>Informaci√≥n Vuelos </p>

      </div>

    );
}

export default Map;
