import Mapa from './Mapa';
import Chat from './chat';
import React, { useState, useEffect } from 'react';
import {io} from "socket.io-client";
import "./map.css";


function App() {
  const [socket, setSocket] = useState(false);
  const[usuario, setUsuario] = useState(false);
  const clickHandler = (value)=> {
    usuario = value;
  }

  useEffect(() => {
    const connect_socket = io("ws://tarea-3-websocket.2021-1.tallerdeintegracion.cl", {path: '/flights'});
    setSocket(connect_socket)
  }, []
  );

  if (socket){
    if (usuario){
      return (
        <div>    

          <div >

            <Chat id="chat" socket={socket} user={usuario}></Chat>
            <Mapa id="mapa" socket={socket}></Mapa>
          </div>


        }
        </div>

      );
    }
    else {
      console.log(usuario)
      return (
        <div>
           <form  onSubmit={clickHandler}>
                <div >
                    <input type="text" placeholder="Nickname" className="form-control"  name="nickname"></input>
                </div>
                <button type="submit" className="btn btn-primary">Enviar</button>
            </form>
        </div>
      );
    }

  }
  else{return <div>Cargando...</div>}

}


export default App;
