import React, { useState, useEffect } from "react";

const Chat = ({socket})=> {
  const socket_recibido = socket[0];
  const name = socket[1];

    const [chat, setChat] = useState([]);
    useEffect(() => {

      socket_recibido.emit("CHAT", {name:"Elisa", date:"ahora", message:"hola"})
      socket_recibido.on("CHAT", (data) => {
        setChat(data)
      });
    }, []);
    return (

       <h3> Chat:{name} </h3>
      //{chat.map(mensaje => <div>{mensaje.name}</div>)}

    );
}

export default Chat;
